CREATE TABLE realestate_agents (
    agent_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    phone VARCHAR(20),
    email VARCHAR(100)
);

CREATE TABLE realestate_listings (
    listing_id INT PRIMARY KEY,
    agent_id INT,
    address VARCHAR(255),
    city VARCHAR(100),
    state VARCHAR(50),
    price DECIMAL(12,2),
    bed INT,
    bath DECIMAL(3,1),
    square_feet INT,
    listed_date DATE,
    description TEXT,
    FOREIGN KEY (agent_id) REFERENCES realestate_agents(agent_id)
);

